report.query:
DROP TABLE IF EXISTS filtered_occurrences; DROP TABLE IF EXISTS output_rows;
SELECT o.* INTO TEMPORARY filtered_occurrences FROM cache_occurrences_functional o #joins# WHERE 1=1 #filters# #order_by# LIMIT #limit#;
SELECT #columns# INTO TEMPORARY output_rows FROM filtered_occurrences o JOIN cache_occurrences_nonfunctional onf ON onf.id=o.id JOIN occurrences occ on occ.id=o.id AND occ.deleted=false JOIN cache_samples_functional s ON s.id=o.sample_id JOIN cache_samples_nonfunctional snf ON snf.id=o.sample_id JOIN cache_taxa_taxon_lists cttl ON cttl.id=o.taxa_taxon_list_id LEFT JOIN locations l ON l.id=o.location_id AND l.deleted=false AND o.private=false AND o.sensitive=false -- Recorder supplied higher geography locations LEFT JOIN locations shg ON shg.id=snf.attr_linked_location_id AND shg.deleted=false AND o.private=false AND o.sensitive=false LEFT JOIN cache_termlists_terms shgt on shgt.id=shg.location_type_id LEFT JOIN dna_occurrences dnao ON dnao.occurrence_id=o.id AND dnao.deleted=false;
-- Parent sample data. UPDATE output_rows o SET recorded_parent_location_id = CASE WHEN o.private='false' AND o.sensitive='false' THEN lp.id ELSE null END, recorded_parent_location_name = CASE WHEN o.private='false' AND o.sensitive='false' THEN lp.name ELSE null END, recorded_parent_location_code = CASE WHEN o.private='false' AND o.sensitive='false' THEN lp.code ELSE null END, given_parent_locality_name = CASE WHEN o.private='false' AND o.sensitive='false' THEN COALESCE(lp.name, sp.location_name) ELSE null END, parent_sample_attrs_json=snfp.attrs_json FROM samples sp JOIN cache_samples_nonfunctional snfp ON snfp.id=sp.id LEFT JOIN locations lp ON lp.id=sp.location_id AND lp.deleted=false WHERE sp.id=o.parent_sample_id AND sp.deleted=false;
-- Use parents of locations if no parent sample location. UPDATE output_rows o SET recorded_parent_location_id = lp.id, recorded_parent_location_name = lp.name, recorded_parent_location_code = lp.code FROM samples sc JOIN locations lc ON lc.id=sc.location_id AND lc.deleted=false JOIN locations lp ON lp.id = lc.parent_id AND lp.deleted=false WHERE sc.id=o.sample_id AND sc.deleted=false AND o.recorded_parent_location_id IS NULL AND o.private='false' AND o.sensitive='false';
-- Sensitive site name warnings. UPDATE output_rows o SET given_locality_name='!', given_parent_locality_name = CASE WHEN COALESCE(lp.name, sp.location_name, '')<>'' THEN NULL ELSE '!' END FROM samples s LEFT JOIN samples sp ON sp.id=s.parent_id AND sp.deleted=false LEFT JOIN locations l ON l.id=s.location_id AND l.deleted=false LEFT JOIN locations lp ON lp.id=sp.location_id AND lp.deleted=false WHERE s.id=o.sample_id AND s.deleted=false AND (o.private='true' OR o.sensitive='true') AND COALESCE(l.name, s.location_name, lp.name, sp.location_name, '')<>'';
UPDATE output_rows o SET classifier_current_det_probability_given=cs.probability_given, classifier_current_det_taxon_name_given=cs.taxon_name_given, classifier_current_det_human_chosen=cs.human_chosen::text, classifier_current_det_classifier_chosen=cs.classifier_chosen::text FROM occurrence_media m JOIN classification_results_occurrence_media crom ON crom.occurrence_media_id=m.id JOIN classification_suggestions cs on cs.classification_result_id=crom.classification_result_id AND cs.deleted=false JOIN cache_taxa_taxon_lists cttl on cttl.id=cs.taxa_taxon_list_id WHERE m.occurrence_id=o.id AND m.deleted=false AND cttl.external_key=o.accepted_taxon_key;
UPDATE output_rows o SET classifier_suggestions=( SELECT json_agg(subquery.*) FROM ( SELECT cssub.taxon_name_given, cssub.taxa_taxon_list_id, cssub.probability_given, cssub.human_chosen::text, cssub.classifier_chosen::text, tsub.term as classifier, crsub.classifier_version, to_char(cssub.created_on, 'yyyy-mm-dd HH24:MI:SS.MS') as created_on FROM classification_suggestions cssub JOIN classification_results crsub ON crsub.id=cssub.classification_result_id AND crsub.deleted=false JOIN cache_termlists_terms tsub on tsub.id=crsub.classifier_id WHERE cssub.classification_result_id=crom.classification_result_id ORDER BY cssub.probability_given DESC ) AS subquery )::text, classifier_current_det_probability_given=COALESCE(o.classifier_current_det_probability_given, 0), classifier_current_det_classifier_chosen=COALESCE(o.classifier_current_det_classifier_chosen, 'false'), classifier_current_det_human_chosen=COALESCE(o.classifier_current_det_human_chosen, 'false') FROM classification_results_occurrence_media crom JOIN occurrence_media m ON m.id=crom.occurrence_media_id AND m.deleted=false WHERE m.occurrence_id=o.id;
UPDATE output_rows o SET verifier_comment=oc.comment FROM occurrence_comments oc WHERE oc.occurrence_id=o.id AND oc.deleted=false AND oc.created_on>=o.verified_on::timestamp AND oc.record_status IS NOT NULL AND oc.record_status=o.record_status AND COALESCE(oc.record_substatus, 0)=COALESCE(o.record_substatus, 0);
SELECT * FROM output_rows o #order_by#
