report.query:
DROP TABLE IF EXISTS filtered_samples; DROP TABLE IF EXISTS occurrence_stats; DROP TABLE IF EXISTS output_rows; DROP TABLE IF EXISTS sample_occurrences_list;
SELECT s.* INTO TEMPORARY filtered_samples FROM cache_samples_functional s #agreements_join# #joins# WHERE #sharing_filter# AND (s.sensitive=true OR s.private=true) #filters# #order_by# LIMIT #limit#;
SELECT DISTINCT s.id as sample_id, o.id as occurrence_id, o.taxa_taxon_list_id, o.confidential, o.release_status INTO TEMPORARY sample_occurrences_list FROM filtered_samples s JOIN cache_occurrences_functional o ON o.sample_id=s.id OR o.parent_sample_id=s.id;
SELECT s.id as sample_id, COUNT(DISTINCT ol.occurrence_id) AS count_occurrences, COUNT(distinct cttl.taxon_meaning_id) AS count_taxa, COUNT(distinct cttl.taxon_group_id) AS count_taxon_groups, CASE COUNT(DISTINCT ol.occurrence_id) WHEN 0 THEN 0 ELSE SUM(case when onf.attr_sex_stage_count similar to '[0-9]{1,9}' then onf.attr_sex_stage_count::integer else null end) END AS sum_individual_count, MAX(onf.sensitivity_precision) AS max_sensitivity_precision, COALESCE(BOOL_OR(ol.confidential), false) AS any_confidential, string_agg(DISTINCT ol.release_status, '') as all_release_status INTO TEMPORARY occurrence_stats FROM filtered_samples s LEFT JOIN sample_occurrences_list ol ON ol.sample_id=s.id LEFT JOIN cache_occurrences_nonfunctional onf ON onf.id=ol.occurrence_id LEFT JOIN cache_taxa_taxon_lists cttl ON cttl.id=ol.taxa_taxon_list_id GROUP BY s.id;
SELECT #columns# INTO TEMPORARY output_rows FROM filtered_samples s JOIN samples smp ON smp.id=s.id JOIN occurrence_stats os ON os.sample_id=s.id JOIN cache_samples_nonfunctional snf ON snf.id=s.id LEFT JOIN locations l ON l.id=smp.location_id AND l.deleted=false;
UPDATE output_rows o SET recorded_parent_location_id = lp.id, recorded_parent_location_name = lp.name, recorded_parent_location_code = lp.code, parent_sample_attrs_json=snfp.attrs_json FROM samples sp JOIN cache_samples_nonfunctional snfp ON snfp.id=sp.id LEFT JOIN locations lp ON lp.id=sp.location_id AND lp.deleted=false WHERE sp.id=o.parent_sample_id AND sp.deleted=false;
-- Use parents of locations if no parent sample location. UPDATE output_rows o SET recorded_parent_location_id = lp.id, recorded_parent_location_name = lp.name, recorded_parent_location_code = lp.code FROM samples sc JOIN locations lc ON lc.id=sc.location_id AND lc.deleted=false JOIN locations lp ON lp.id = lc.parent_id AND lp.deleted=false WHERE sc.id=o.sample_id AND sc.deleted=false AND o.recorded_parent_location_id IS NULL;
SELECT * FROM output_rows s #order_by#
